```tsx
import { FormEvent, useState } from "react";
import { Task } from "../types/task";

type TaskInputProps = {
  onAddTask: (title: string, notes: string) => void;
};

export const TaskInput = ({ onAddTask }: TaskInputProps) => {
  const [title, setTitle] = useState("");
  const [notes, setNotes] = useState("");

  const handleSubmit = (event: FormEvent) => {
    event.preventDefault();
    const normalizedTitle = title.trim();
    if (!normalizedTitle) {
      return;
    }

    onAddTask(normalizedTitle, notes.trim());
    setTitle("");
    setNotes("");
  };

  return (
    <form onSubmit={handleSubmit} className="task-input">
      <label>
        Task title
        <input
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          placeholder="Add a task..."
        />
      </label>
      <label>
        Notes
        <textarea
          value={notes}
          onChange={(event) => setNotes(event.target.value)}
          placeholder="Optional notes"
          rows={3}
        />
      </label>
      <button type="submit" disabled={!title.trim()}>
        Add task
      </button>
    </form>
  );
};
```

`frontend/src/components/TaskItem.tsx`
```tsx
import { Task } from "../types/task";

type TaskItemProps = {
  task: Task;
  onToggleDone: (taskId: string) => void;
};

export const TaskItem = ({ task, onToggleDone }: TaskItemProps) => (
  <li className="task-item">
    <label>
      <input
        type="checkbox"
        checked={task.completed}
        onChange={() => onToggleDone(task.id)}
      />
      <span className={task.completed ? "completed" : undefined}>{task.title}</span>
    </label>
    {task.notes && <p className="task-notes">{task.notes}</p>}
  </li>
);
```

`frontend/src/components/TaskList.tsx`
```tsx
import { Task } from "../types/task";
import { TaskItem } from "./TaskItem";

type TaskListProps = {
  tasks: Task[];
  onToggleDone: (taskId: string) => void;
};

export const TaskList = ({ tasks, onToggleDone }: TaskListProps) => {
  if (tasks.length === 0) {
    return <p className="empty-state">No tasks yet. Add one to get started.</p>;
  }

  return (
    <ul className="task-list">
      {tasks.map((task) => (
        <TaskItem key={task.id} task={task} onToggleDone={onToggleDone} />
      ))}
    </ul>
  );
};
```

