`frontend/src/routes/TasksRoute.tsx`
```tsx
import { useState } from "react";
import { initialTasks } from "../data/mockTasks";
import { Task } from "../types/task";
import { TaskInput } from "../components/TaskInput";
import { TaskList } from "../components/TaskList";

const createMockTask = (title: string, notes: string): Task => ({
  id: `task-${Date.now()}-${Math.random().toString(36).slice(2, 8)}`,
  title,
  notes: notes || undefined,
  completed: false,
});

export const TasksRoute = () => {
  const [tasks, setTasks] = useState(initialTasks);

  return (
    <section className="tasks-route">
      <h2>Task Center</h2>
      <TaskInput
        onAddTask={(title, notes) => {
          setTasks((prev) => [createMockTask(title, notes), ...prev]);
        }}
      />
      <TaskList
        tasks={tasks}
        onToggleDone={(taskId) => {
          setTasks((prev) =>
            prev.map((task) =>
              task.id === taskId ? { ...task, completed: !task.completed } : task
            )
          );
        }}
      />
    </section>
  );
};
```

`frontend/src/App.tsx`
```tsx
import { Navigate, Route, Routes } from "react-router-dom";
import { AppShell } from "./layouts/AppShell";
import { TasksRoute } from "./routes/TasksRoute";

export const App = () => (
  <Routes>
    <Route element={<AppShell />}>
      <Route index element={<Navigate to="/tasks" replace />} />
      <Route path="/tasks" element={<TasksRoute />} />
      <Route path="*" element={<p className="route-fallback">Page not found.</p>} />
    </Route>
  </Routes>
);
```

