  { href: "/tasks/new", label: "Create Task" },
];

export default function App({ Component, pageProps }: AppProps) {
  return (
    <>
      <div className="tasks-ui-shell">
        <header className="top-nav">
          <div className="brand">Tasks UI</div>
          <nav>
            {navItems.map((item) => (
